CREATE TABLE `sys_oauth_client`  (
  `id` varchar(32) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT 'ID',
  `create_by` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '创建人',
  `create_time` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `update_by` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '更新人',
  `update_time` datetime NULL DEFAULT NULL COMMENT '更新时间',
  `access_token_timeout` bigint NULL DEFAULT NULL COMMENT 'Access-Token 保存的时间（单位：秒）',
  `allow_url` text CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL COMMENT '应用允许授权的所有URL, 多个用逗号隔开 （可以使用 * 号通配符）',
