DROP TABLE IF EXISTS `document`;
CREATE TABLE `document`  (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '唯一标识',
  `debug` tinyint NOT NULL DEFAULT 0 COMMENT '是否为 DEBUG 调试数据，只允许在开发环境使用，测试和线上环境禁用：0-否，1-是。',
  `userId` bigint NOT NULL COMMENT '管理员用户id。\n需要先建Admin表，新增登录等相关接口。',
  `testAccountId` bigint NOT NULL DEFAULT 0 COMMENT '测试账号id。0-不限',
  `version` tinyint NOT NULL DEFAULT 3 COMMENT '接口版本号\n<=0 - 不限制版本，任意版本都可用这个接口；\n>0 - 在这个版本添加的接口。\n\n可在给新版文档前调高默认值，新增的测试用例就不用手动设置版本号了。',
  `name` varchar(100) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '接口名称',
  `type` varchar(5) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL DEFAULT 'JSON' COMMENT 'PARAM - GET  url parameters,\nFORM - POST  application/www-x-form-url-encoded,\nJSON - POST  application/json',
  `url` varchar(250) CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '请求地址',
  `request` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NOT NULL COMMENT '请求\n用json格式会导致强制排序，而请求中引用赋值只能引用上面的字段，必须有序。',
  `apijson` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL COMMENT '从 request 映射为实际的 APIJSON 请求 JSON',
  `sqlauto` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL COMMENT '为 SQLAuto 留的字段，格式为\\\\\\\\n{“sql”:”SELECT * FROM `sys`.`Comment` LIMIT ${limit}”,”arg”:”limit: 3”}',
  `standard` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL,
  `header` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL COMMENT '请求头 Request Header：\nkey: value  //注释',
  `date` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建日期',
  `detail` text CHARACTER SET utf8mb3 COLLATE utf8mb3_general_ci NULL COMMENT '详细的说明，可以是普通文本或 Markdown 格式文本',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8mb3 COLLATE = utf8mb3_general_ci COMMENT = '测试用例文档\n后端开发者在测试好后，把选好的测试用例上传，这样就能共享给前端/客户端开发者' ROW_FORMAT = DYNAMIC;

